package day19;

public class WhileLoopMorePractice {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		//Odd numbers Printer from 1- 50
		int number = 1;
		
		//while loop to check numbers between 1 to 50
		while (number <= 50) {
		if (number % 2 != 0) {
			System.out.println(number +" I love Blueberry!");
		}
		number ++;
		}
	}

}
